import { useEffect, useState } from "react";
import { CiSearch } from "react-icons/ci";
import "./SearchBar.scss";
import { Octokit } from "@octokit/rest";

function SearchBar() {
  const [userValue, setUserValue] = useState();
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [user, setUser] = useState(null);

  const handleUserChange = (e) => {
    setUserValue(e.target.value);
  };

  // const octocitK = "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";
  // const octokitA = new Octokit({
  //   auth: octocitK,
  //   userAgent: "CipHer170",
  // });
  // console.log(octokitA);

  const handleSubmitSearch = () => {
    getData(userValue);
  };

  const getData = (username) => {
    fetch(`https://api.github.com/users/${username}`)
      .then((res) => res.json)
      .then(
        (result) => {
          if (result.message !== undefined) {
            setIsLoaded(true);
            setError(result);
          } else {
            setIsLoaded(true);
            setUser(result);
            setError(null);
          }
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  };

  useEffect(() => {
    getData();
  }, []);
  return (
    <div className="searchbar">
      <p>
        <CiSearch />
      </p>
      <input
        name="searchbar"
        id="searchbar"
        className="searchbar__input input"
        value={userValue}
        onChange={handleUserChange}
        placeholder="Search GitHub username â€¦"
      />
      <button className="searchbar__btn btn" onCl={handleSubmitSearch}>
        Search
      </button>
    </div>
  );
}

export default SearchBar;
